import React from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import { BrowserRouter, Route, Routes, useLocation } from 'react-router-dom';
import Home from './pages/Home/HomePage';
import NewFeed from './pages/NewFeed/NewFeedPage';
import Event from './pages/Event/EventPage';
import Shop from './pages/Shop/ShopPage';
import Knowledge from './pages/Knowledge/KnowledgePage';
import Login from './pages/Auth/Login';

function AppContent() {
  const location = useLocation();
  const isLogin = location.pathname === "/login";

  return (
    <>
      {!isLogin && <Header />}
      <div className="app-main-content" style={{ position: "relative", minHeight: "100vh" }}>
        {/* HomePage luôn ở dưới */}
        <div className={isLogin ? "blurred-bg" : ""}>
          <Routes location={isLogin ? { pathname: "/" } : location}>
            <Route path="/" element={<Home />} />
            <Route path="/newfeed" element={<NewFeed />} />
            <Route path="/event" element={<Event />} />
            <Route path="/shop" element={<Shop />} />
            <Route path="/knowledge" element={<Knowledge />} />
          </Routes>
        </div>
   
        {isLogin && (
          <div className="login-overlay">
            <Login />
          </div>
        )}
      </div>
      {!isLogin && <Footer />}
    </>
  );
}

function App() {
  return (
    <BrowserRouter>
      <AppContent />
    </BrowserRouter>
  );
}

export default App;
