import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Alert,
  Spinner,
  Stack,
  Offcanvas,
  ListGroup,
} from "react-bootstrap";
import { useAuth } from "../Auth/AuthContext";

export default function Shop() {
  const { user, logout } = useAuth();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCart, setShowCart] = useState(false);
  const [cart, setCart] = useState(() => {
    return JSON.parse(localStorage.getItem("cart")) || [];
  });

  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:9999/shop")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Error fetching products:", err))
      .finally(() => setLoading(false));
  }, []);

  const updateCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
  };

  const handleBuyClick = (product) => {
    if (!user) {
      alert("Bạn cần đăng nhập để mua sản phẩm.");
      navigate("/login");
      return;
    }

    const existingCart = JSON.parse(localStorage.getItem("cart")) || [];
    const index = existingCart.findIndex((item) => item.id === product.id);

    if (index >= 0) {
      existingCart[index].quantity += 1;
    } else {
      existingCart.push({ ...product, quantity: 1 });
    }

    updateCart(existingCart);
    alert("Đã thêm vào giỏ hàng!");
  };

  const handleRemoveItem = (id) => {
    const newCart = cart.filter((item) => item.id !== id);
    updateCart(newCart);
  };

  return (
    <Container className="mt-4">
      {/* ✅ Cart Offcanvas */}
      <Offcanvas show={showCart} onHide={() => setShowCart(false)} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>🛒 Giỏ hàng</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          {cart.length === 0 ? (
            <p>Giỏ hàng đang trống.</p>
          ) : (
            <ListGroup>
              {cart.map((item) => (
                <ListGroup.Item key={item.id} className="d-flex justify-content-between align-items-start">
                  <div>
                    <strong>{item.name}</strong>
                    <br />
                    SL: {item.quantity} – {item.price.toLocaleString()} VND
                  </div>
                  <Button
                    size="sm"
                    variant="outline-danger"
                    onClick={() => handleRemoveItem(item.id)}
                  >
                    X
                  </Button>
                </ListGroup.Item>
              ))}
            </ListGroup>
          )}
        </Offcanvas.Body>
      </Offcanvas>

      {/* ✅ Top bar */}
      <Stack direction="horizontal" gap={2} className="mb-3 justify-content-end">
        <Button variant="outline-success" size="sm" onClick={() => setShowCart(true)}>
          🛒 Xem giỏ hàng ({cart.length})
        </Button>
        {user ? (
          <>
            <span>Xin chào, {user.username}</span>
            <Button variant="outline-danger" size="sm" onClick={logout}>
              Đăng xuất
            </Button>
          </>
        ) : (
          <Button variant="outline-primary" size="sm" onClick={() => navigate("/login")}>
            Đăng nhập
          </Button>
        )}
      </Stack>

      <h1 className="text-center mb-4">🛍️ Shop</h1>
      <h4 className="mb-4 text-muted">Sản phẩm nổi bật</h4>

      {loading ? (
        <div className="d-flex justify-content-center my-5">
          <Spinner animation="border" />
        </div>
      ) : products.length === 0 ? (
        <Alert variant="warning">Không có sản phẩm nào.</Alert>
      ) : (
        <Row xs={1} sm={2} md={3} lg={4} className="g-4">
          {products.map((product) => (
            <Col key={product.id}>
              <Card className="h-100 shadow-sm">
                <Card.Img
                  variant="top"
                  src={product.image}
                  alt={product.name}
                  style={{ height: "180px", objectFit: "cover" }}
                />
                <Card.Body>
                  <Card.Title>{product.name}</Card.Title>
                  <Card.Text style={{ fontSize: "0.9rem" }}>{product.description}</Card.Text>
                  <Card.Text>
                    <strong>Giá:</strong> {product.price.toLocaleString()} VND
                    <br />
                    <strong>Còn lại:</strong> {product.StockQuantity}
                  </Card.Text>
                </Card.Body>
                <Card.Footer className="bg-white border-top-0">
                  <Button
                    variant="primary"
                    className="w-100"
                    onClick={() => handleBuyClick(product)}
                  >
                    Mua
                  </Button>
                </Card.Footer>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
