import React, { useEffect, useState } from "react";
import { Container, Table, Button, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function Cart() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
    calculateTotal(storedCart);
  }, []);

  const calculateTotal = (items) => {
    const sum = items.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotal(sum);
  };

  const handleRemove = (id) => {
    const updatedCart = [...cart];
    const index = updatedCart.findIndex((item) => item.id === id);

    if (index >= 0) {
      if (updatedCart[index].quantity > 1) {
        updatedCart[index].quantity -= 1;
      } else {
        updatedCart.splice(index, 1);
      }

      setCart(updatedCart);
      localStorage.setItem("cart", JSON.stringify(updatedCart));
      calculateTotal(updatedCart);
      window.dispatchEvent(new CustomEvent("cartUpdated"));
    }
  };

  const handleCheckout = async () => {
  const user = JSON.parse(localStorage.getItem("user"));
  if (!user) {
    alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ thanh to√°n.");
    return;
  }

  if (cart.length === 0) {
    alert("Gi·ªè h√†ng tr·ªëng.");
    return;
  }

  try {
    const totalAmount = cart.reduce(
      (acc, item) => acc + item.price * item.quantity,
      0
    );

    const newOrder = {
      userId: user.id,
      orderDate: new Date().toISOString(),
      statusId: 1,
      totalAmount,
    };

    const orderRes = await fetch("http://localhost:3000/orders", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newOrder),
    });

    if (!orderRes.ok) {
      const msg = await orderRes.text();
      console.error("Order creation failed:", msg);
      throw new Error("T·∫°o ƒë∆°n h√†ng th·∫•t b·∫°i");
    }

    const savedOrder = await orderRes.json();

    // üßæ Ensure we post correct fields
    const detailPosts = cart.map((item) => {
      const detail = {
        orderId: savedOrder.id,
        productId: item.productId ?? item.id, // <-- VERY IMPORTANT
        quantity: item.quantity,
        unitPrice: item.price,
      };
      console.log("Posting orderDetail:", detail);
      return fetch("http://localhost:3000/orderDetails", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(detail),
      });
    });

    await Promise.all(detailPosts);

    localStorage.removeItem("cart");
    setCart([]);
    setTotal(0);
    window.dispatchEvent(new CustomEvent("cartUpdated"));

    alert("ƒê·∫∑t h√†ng th√†nh c√¥ng!");
    navigate("/orderConfirm");
  } catch (error) {
    console.error("Checkout error:", error);
    alert("C√≥ l·ªói x·∫£y ra khi g·ª≠i ƒë∆°n h√†ng.");
  }
};

  return (
    <Container className="mt-4">
      <h1 className="mb-4">üõí Gi·ªè h√†ng c·ªßa b·∫°n</h1>

      {cart.length === 0 ? (
        <Alert variant="info">Gi·ªè h√†ng ƒëang tr·ªëng.</Alert>
      ) : (
        <>
          <Table bordered hover responsive>
            <thead className="table-primary">
              <tr>
                <th>#</th>
                <th>S·∫£n ph·∫©m</th>
                <th>Gi√°</th>
                <th>S·ªë l∆∞·ª£ng</th>
                <th>Th√†nh ti·ªÅn</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item, index) => (
                <tr key={item.id}>
                  <td>{index + 1}</td>
                  <td>{item.name}</td>
                  <td>{item.price.toLocaleString()} VND</td>
                  <td>{item.quantity}</td>
                  <td>{(item.price * item.quantity).toLocaleString()} VND</td>
                  <td>
                    <Button
                      variant="outline-danger"
                      size="sm"
                      onClick={() => handleRemove(item.id)}
                    >
                      Xo√°
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>

          <h4 className="text-end">
            T·ªïng c·ªông: <span className="text-primary">{total.toLocaleString()} VND</span>
          </h4>

          <div className="d-flex justify-content-end mt-3">
            <Button variant="success" onClick={handleCheckout}>
              Thanh To√°n
            </Button>
          </div>
        </>
      )}
    </Container>
  );
}

export default Cart;
