import React from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import { Routes, Route } from 'react-router-dom';
import Home from './pages/Home/HomePage';
import NewFeed from './pages/NewFeed/NewFeedPage';
import Event from './pages/Event/EventPage';
import Shop from './pages/Shop/ShopPage';
import Knowledge from './pages/Knowledge/KnowledgePage';
import Login from './pages/Auth/Login';
import { useAuth } from './pages/Auth/AuthContext';

function App() {
  const { user } = useAuth();

  if (!user) {
    // Nếu chưa đăng nhập, chỉ hiện trang Login
    return <Login />;
  }

  // Nếu đã đăng nhập, hiện toàn bộ app
  return (
    <>
      <Header />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/newfeed" element={<NewFeed />} />
        <Route path="/event" element={<Event />} />
        <Route path="/shop" element={<Shop />} />
        <Route path="/knowledge" element={<Knowledge />} />
      </Routes>
      <Footer />
    </>
  );
}

export default App;
