import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { Container, Card, ListGroup, Button } from "react-bootstrap";

export default function EventParticipants() {
    const { id } = useParams();
    const [participants, setParticipants] = useState([]);
    const [eventTitle, setEventTitle] = useState("");

    useEffect(() => {
        // Lấy tên sự kiện
        fetch(`http://localhost:9999/events/${id}`)
            .then(res => res.json())
            .then(event => setEventTitle(event.title));

        // Lấy danh sách đăng ký theo eventId
        fetch(`http://localhost:9999/registrations?eventId=${id}`)
            .then(res => res.json())
            .then(data => setParticipants(data));
    }, [id]);

    return (
        <Container className="py-4">
            <Card>
                <Card.Header>
                    <h5>Danh sách người tham gia: {eventTitle}</h5>
                </Card.Header>
                <Card.Body>
                    {participants.length === 0 ? (
                        <div className="text-muted">Chưa có người tham gia</div>
                    ) : (
                        <ListGroup>
                            {participants.map((user, idx) => (
                                <ListGroup.Item key={user.id || idx}>
                                    <div><b>Họ tên:</b> {user.name}</div>
                                    <div><b>Email:</b> {user.email}</div>
                                    <div><b>Điện thoại:</b> {user.phone}</div>
                                    <div><b>Tuổi:</b> {user.age}</div>
                                    <div><b>Giới tính:</b> {user.gender}</div>
                                    <div><b>Ngày đăng ký:</b> {new Date(user.registeredAt).toLocaleString("vi-VN")}</div>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                    )}
                    <Button as={Link} to="/eventsmanager" variant="secondary" className="mt-3">
                        Quay lại
                    </Button>
                </Card.Body>
            </Card>
        </Container>
    );
}
