import React, { useEffect, useState } from "react";
import { Container, Table, Button, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

function Cart() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
    calculateTotal(storedCart);
  }, []);

  const calculateTotal = (items) => {
    const sum = items.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotal(sum);
  };

  const handleRemove = (id) => {
    const updatedCart = [...cart];
    const index = updatedCart.findIndex((item) => item.id === id);

    if (index >= 0) {
      if (updatedCart[index].quantity > 1) {
        updatedCart[index].quantity -= 1;
      } else {
        updatedCart.splice(index, 1);
      }

      setCart(updatedCart);
      localStorage.setItem("cart", JSON.stringify(updatedCart));
      calculateTotal(updatedCart);
      window.dispatchEvent(new CustomEvent("cartUpdated"));
    }
  };

  const handleCheckout = async () => {
  const user = JSON.parse(localStorage.getItem("user"));
  if (!user) {
    alert("B·∫°n c·∫ßn ƒëƒÉng nh·∫≠p ƒë·ªÉ thanh to√°n.");
    return;
  }

  try {
    // Step 1: Fetch cart from json-server
    const cartRes = await fetch(`http://localhost:3000/shoppingCart?userId=${user.id}`);
    const cartItems = await cartRes.json();

    if (cartItems.length === 0) {
      alert("Gi·ªè h√†ng tr·ªëng.");
      return;
    }

    // Step 2: Calculate total
    const totalAmount = cartItems.reduce((acc, item) => acc + item.quantity * item.unitPrice, 0);

    // Step 3: Create the order
    const newOrder = {
      userId: user.id,
      orderDate: new Date().toISOString(),
      statusId: 1,
      totalAmount,
    };

    const orderRes = await fetch("http://localhost:3000/orders", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newOrder),
    });

    if (!orderRes.ok) throw new Error("T·∫°o ƒë∆°n h√†ng th·∫•t b·∫°i");

    const savedOrder = await orderRes.json();

    // Step 4: Add orderDetails for each item
    await Promise.all(
      cartItems.map((item) =>
        fetch("http://localhost:3000/orderDetails", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            orderId: savedOrder.id,
            productId: item.productId,
            quantity: item.quantity,
            unitPrice: item.unitPrice || 0, // fallback
          }),
        })
      )
    );

    // Step 5: Clear shoppingCart entries for this user
    await Promise.all(
      cartItems.map((item) =>
        fetch(`http://localhost:3000/shoppingCart/${item.id}`, {
          method: "DELETE",
        })
      )
    );

    alert("ƒê·∫∑t h√†ng th√†nh c√¥ng!");
    window.dispatchEvent(new CustomEvent("cartUpdated"));
    navigate("/orderConfirm");

  } catch (error) {
    console.error("Checkout error:", error);
    alert("C√≥ l·ªói x·∫£y ra khi g·ª≠i ƒë∆°n h√†ng.");
  }
};


  return (
    <Container className="mt-4">
      <h1 className="mb-4">üõí Gi·ªè h√†ng c·ªßa b·∫°n</h1>

      {cart.length === 0 ? (
        <Alert variant="info">Gi·ªè h√†ng ƒëang tr·ªëng.</Alert>
      ) : (
        <>
          <Table bordered hover responsive>
            <thead className="table-primary">
              <tr>
                <th>#</th>
                <th>S·∫£n ph·∫©m</th>
                <th>Gi√°</th>
                <th>S·ªë l∆∞·ª£ng</th>
                <th>Th√†nh ti·ªÅn</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item, index) => (
                <tr key={item.id}>
                  <td>{index + 1}</td>
                  <td>{item.name}</td>
                  <td>{item.price.toLocaleString()} VND</td>
                  <td>{item.quantity}</td>
                  <td>{(item.price * item.quantity).toLocaleString()} VND</td>
                  <td>
                    <Button
                      variant="outline-danger"
                      size="sm"
                      onClick={() => handleRemove(item.id)}
                    >
                      Xo√°
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>

          <h4 className="text-end">
            T·ªïng c·ªông:{" "}
            <span className="text-primary">{total.toLocaleString()} VND</span>
          </h4>

          <div className="d-flex justify-content-end mt-3">
            <Button variant="success" onClick={handleCheckout}>
              Thanh To√°n
            </Button>
          </div>
        </>
      )}
    </Container>
  );
}

export default Cart;
