import { useState } from "react";
import { Container, Row, Col, Button, Badge } from "react-bootstrap";
import EventRegisterForm from "./EventRegisterForm";

// Giả sử bạn truyền event qua props hoặc lấy từ API
export default function EventDetail({ event }) {
    const [showRegister, setShowRegister] = useState(false);

    if (!event) return <div>Không tìm thấy sự kiện.</div>;

    return (
        <Container className="py-4">
            <Row>
                <Col md={6}>
                    <img src={event.image} alt={event.title} className="img-fluid rounded-4 mb-3" />
                </Col>
                <Col md={6}>
                    <h2>{event.title}</h2>
                    <Badge bg="info" className="mb-2">{event.status}</Badge>
                    <div className="mb-2"><strong>Thời gian:</strong> {event.date}</div>
                    <div className="mb-2"><strong>Địa điểm:</strong> {event.location}</div>
                    <div className="mb-2"><strong>Khu vực:</strong> {event.region === "north" ? "Miền Bắc" : event.region === "central" ? "Miền Trung" : "Miền Nam"}</div>
                    <div className="mb-2"><strong>Loại:</strong> {event.type === "tournament" ? "Giải đấu" : event.type === "workshop" ? "Workshop" : event.type === "festival" ? "Ngày hội" : "Khác"}</div>
                    <div className="mb-2"><strong>Phí tham gia:</strong> {event.fee === 0 ? "Miễn phí" : event.fee.toLocaleString() + " VNĐ"}</div>
                    <div className="mb-2"><strong>Số người:</strong> {event.participants}/{event.maxParticipants}</div>
                    <div className="mb-2">
                        {event.hasPrize && <Badge bg="success" className="me-2">Có giải thưởng</Badge>}
                        {event.forBeginner && <Badge bg="primary" className="me-2">Cho người mới</Badge>}
                        {event.forFamily && <Badge bg="warning" text="dark">Cho gia đình</Badge>}
                    </div>
                    <p className="mt-3">{event.description}</p>
                    <Button variant="primary" onClick={() => setShowRegister(true)}>
                        Đăng ký tham gia
                    </Button>
                </Col>
            </Row>
            {showRegister && (
                <Row className="mt-4">
                    <Col md={8} className="mx-auto">
                        <EventRegisterForm event={event} onClose={() => setShowRegister(false)} />
                    </Col>
                </Row>
            )}
        </Container>
    );
}
