function OrderHistory() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [orders, setOrders] = useState([]);
  const [detailsMap, setDetailsMap] = useState({}); // Map orderId -> list of details

  const getStatusName = (statusId) => {
    switch (statusId) {
      case 1: return "Ch·ªù duy·ªát";
      case 2: return "ƒê√£ duy·ªát";
      case 3: return "Ho√†n th√†nh";
      case 4: return "ƒê√£ thanh to√°n";
      case 5: return "ƒê√£ h·ªßy";
      default: return "Kh√¥ng r√µ";
    }
  };

  useEffect(() => {
    if (!user) return navigate("/login");

    fetch(`http://localhost:9999/orders?userId=${user.id}&_sort=id&_order=desc`)
      .then((res) => res.json())
      .then(async (ordersData) => {
        setOrders(ordersData);

        // Fetch all order details for each order
        const allDetails = {};
        for (const order of ordersData) {
          const resDetails = await fetch(`http://localhost:9999/orderDetails?orderId=${order.id}`);
          const dataDetails = await resDetails.json();
          allDetails[order.id] = dataDetails;
        }
        setDetailsMap(allDetails);
      })
      .catch((err) => console.error("Error fetching orders:", err));
  }, [user]);

  const handleCancel = async (orderId) => {
    const res = await fetch(`http://localhost:9999/orders/${orderId}`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ statusId: 5 }),
    });

    if (res.ok) {
      setOrders(prev =>
        prev.map(o => (o.id === orderId ? { ...o, statusId: 5 } : o))
      );
      alert("ƒê∆°n h√†ng ƒë√£ ƒë∆∞·ª£c h·ªßy.");
    } else {
      alert("H·ªßy ƒë∆°n h√†ng th·∫•t b·∫°i.");
    }
  };

  return (
    <Container className="mt-5 pt-5">
      <h2 className="mb-4">üì¶ L·ªãch s·ª≠ ƒë∆°n h√†ng</h2>

      {orders.length === 0 ? (
        <Alert variant="info">B·∫°n ch∆∞a c√≥ ƒë∆°n h√†ng n√†o.</Alert>
      ) : (
        orders.map((order) => (
          <div key={order.id} className="mb-5 border p-3 rounded shadow-sm bg-light">
            <h5>M√£ ƒë∆°n h√†ng: #{order.id}</h5>
            <p><strong>Ng√†y ƒë·∫∑t:</strong> {new Date(order.orderDate).toLocaleString()}</p>
            <p><strong>Tr·∫°ng th√°i:</strong> {getStatusName(order.statusId)}</p>

            <Table bordered hover responsive className="mt-3">
              <thead className="table-primary">
                <tr>
                  <th>#</th>
                  <th>ID S·∫£n ph·∫©m</th>
                  <th>S·ªë l∆∞·ª£ng</th>
                  <th>ƒê∆°n gi√°</th>
                  <th>Th√†nh ti·ªÅn</th>
                </tr>
              </thead>
              <tbody>
                {detailsMap[order.id]?.map((item, idx) => (
                  <tr key={item.id}>
                    <td>{idx + 1}</td>
                    <td>{item.productId}</td>
                    <td>{item.quantity}</td>
                    <td>{item.unitPrice.toLocaleString()} VND</td>
                    <td>{(item.quantity * item.unitPrice).toLocaleString()} VND</td>
                  </tr>
                ))}
              </tbody>
            </Table>

            <div className="text-end">
              <h5>
                T·ªïng ti·ªÅn: <span className="text-primary">{order.totalAmount.toLocaleString()} VND</span>
              </h5>
              {order.statusId !== 5 && (
                <Button variant="danger" className="mt-2" onClick={() => handleCancel(order.id)}>
                  H·ªßy ƒë∆°n h√†ng
                </Button>
              )}
            </div>
          </div>
        ))
      )}
    </Container>
  );
}
