import React, { useEffect, useState } from "react";
import {
    Container, Row, Col, Card, Table, Button, Form, Badge, InputGroup
} from "react-bootstrap";

export default function EventManager() {
    const [events, setEvents] = useState([]);
    const [search, setSearch] = useState("");
    const [showFilters, setShowFilters] = useState(false);
    const [filter, setFilter] = useState({
        status: "",
        type: "",
        location: "",
        fromDate: "",
        toDate: ""
    });

    useEffect(() => {
        fetch("http://localhost:9999/events")
            .then(res => res.json())
            .then(data => setEvents(data));
    }, []);

    // Filter logic
    let filteredEvents = events.filter(event => {
        const matchesSearch = event.title.toLowerCase().includes(search.toLowerCase());
        const matchesStatus = filter.status ? event.status === filter.status : true;
        const matchesType = filter.type ? event.type === filter.type : true;
        const matchesLocation = filter.location
            ? event.location.toLowerCase().includes(filter.location.toLowerCase())
            : true;
        const matchesFromDate = filter.fromDate ? new Date(event.startDate) >= new Date(filter.fromDate) : true;
        const matchesToDate = filter.toDate ? new Date(event.endDate) <= new Date(filter.toDate) : true;
        return matchesSearch && matchesStatus && matchesType && matchesLocation && matchesFromDate && matchesToDate;
    });

    return (
        <Container fluid className="py-4 main-content">
            <Row>
                <Col
                    md={2}
                    className="bg-white border-end min-vh-100 p-0 shadow-sm"
                    style={{ boxShadow: "2px 0 5px rgba(0,0,0,0.05)" }}
                >
                    <Nav className="flex-column p-3">
                        <Nav.Link
                            href="#"
                            style={{
                                color: "#0d6efd",
                                fontWeight: 600,
                                padding: "10px 15px",
                                borderRadius: "8px",
                                display: "flex",
                                alignItems: "center",
                                backgroundColor: "#e7f1ff",
                                textDecoration: "none",
                            }}
                        >
                            <i className="ri-home-line me-2" style={{ fontSize: "18px" }}></i>
                            Trang chủ
                        </Nav.Link>

                        <Nav.Link
                            href="#"
                            style={{
                                color: "#6c757d",
                                fontWeight: 500,
                                padding: "10px 15px",
                                borderRadius: "8px",
                                display: "flex",
                                alignItems: "center",
                                transition: "all 0.2s ease",
                                textDecoration: "none",
                            }}
                            onMouseOver={(e) => {
                                e.currentTarget.style.backgroundColor = "#f1f3f5";
                                e.currentTarget.style.color = "#0d6efd";
                            }}
                            onMouseOut={(e) => {
                                e.currentTarget.style.backgroundColor = "transparent";
                                e.currentTarget.style.color = "#6c757d";
                            }}
                        >
                            <i className="ri-calendar-line me-2" style={{ fontSize: "18px" }}></i>
                            Quản lý đặt chỗ
                        </Nav.Link>

                        <Nav.Link
                            href="#"
                            style={{
                                color: "#6c757d",
                                fontWeight: 500,
                                padding: "10px 15px",
                                borderRadius: "8px",
                                display: "flex",
                                alignItems: "center",
                                transition: "all 0.2s ease",
                                textDecoration: "none",
                            }}
                            onMouseOver={(e) => {
                                e.currentTarget.style.backgroundColor = "#f1f3f5";
                                e.currentTarget.style.color = "#0d6efd";
                            }}
                            onMouseOut={(e) => {
                                e.currentTarget.style.backgroundColor = "transparent";
                                e.currentTarget.style.color = "#6c757d";
                            }}
                        >
                            <i className="ri-team-line me-2" style={{ fontSize: "18px" }}></i>
                            Quản lý khách hàng
                        </Nav.Link>

                        <Nav.Link
                            href="/eventsmanager"
                            style={{
                                color: "#6c757d",
                                fontWeight: 500,
                                padding: "10px 15px",
                                borderRadius: "8px",
                                display: "flex",
                                alignItems: "center",
                                transition: "all 0.2s ease",
                                textDecoration: "none",
                            }}
                            onMouseOver={(e) => {
                                e.currentTarget.style.backgroundColor = "#f1f3f5";
                                e.currentTarget.style.color = "#0d6efd";
                            }}
                            onMouseOut={(e) => {
                                e.currentTarget.style.backgroundColor = "transparent";
                                e.currentTarget.style.color = "#6c757d";
                            }}
                        >
                            <i className="ri-calendar-event-line me-2" style={{ fontSize: "18px" }}></i>
                            Quản lý sự kiện
                        </Nav.Link>

                        <Nav.Link
                            href="#"
                            style={{
                                color: "#6c757d",
                                fontWeight: 500,
                                padding: "10px 15px",
                                borderRadius: "8px",
                                display: "flex",
                                alignItems: "center",
                                transition: "all 0.2s ease",
                                textDecoration: "none",
                            }}
                            onMouseOver={(e) => {
                                e.currentTarget.style.backgroundColor = "#f1f3f5";
                                e.currentTarget.style.color = "#0d6efd";
                            }}
                            onMouseOut={(e) => {
                                e.currentTarget.style.backgroundColor = "transparent";
                                e.currentTarget.style.color = "#6c757d";
                            }}
                        >
                            <i className="ri-file-chart-line me-2" style={{ fontSize: "18px" }}></i>
                            Báo cáo
                        </Nav.Link>

                        <Nav.Link
                            href="#"
                            style={{
                                color: "#6c757d",
                                fontWeight: 500,
                                padding: "10px 15px",
                                borderRadius: "8px",
                                display: "flex",
                                alignItems: "center",
                                transition: "all 0.2s ease",
                                textDecoration: "none",
                            }}
                            onMouseOver={(e) => {
                                e.currentTarget.style.backgroundColor = "#f1f3f5";
                                e.currentTarget.style.color = "#0d6efd";
                            }}
                            onMouseOut={(e) => {
                                e.currentTarget.style.backgroundColor = "transparent";
                                e.currentTarget.style.color = "#6c757d";
                            }}
                        >
                            <i className="ri-settings-line me-2" style={{ fontSize: "18px" }}></i>
                            Cài đặt
                        </Nav.Link>
                    </Nav>
                </Col>
                <Col md={10}>
                    <h2 className="mb-4">Danh sách sự kiện</h2>
                    <Card className="mb-4">
                        <Card.Body>
                            <Row className="align-items-center mb-3">
                                <Col md={4}>
                                    <InputGroup>
                                        <Form.Control
                                            placeholder="Tìm kiếm sự kiện..."
                                            value={search}
                                            onChange={e => setSearch(e.target.value)}
                                        />
                                        <Button variant="outline-secondary" onClick={() => setShowFilters(f => !f)}>
                                            Bộ lọc
                                        </Button>
                                    </InputGroup>
                                </Col>
                            </Row>
                            {showFilters && (
                                <Row className="mb-3">
                                    <Col md={2}>
                                        <Form.Label>Trạng thái</Form.Label>
                                        <Form.Select
                                            value={filter.status}
                                            onChange={e => setFilter(f => ({ ...f, status: e.target.value }))}
                                        >
                                            <option value="">Tất cả</option>
                                            <option value="Sắp diễn ra">Sắp diễn ra</option>
                                            <option value="Đang diễn ra">Đang diễn ra</option>
                                            <option value="Đã kết thúc">Đã kết thúc</option>
                                        </Form.Select>
                                    </Col>
                                    <Col md={2}>
                                        <Form.Label>Loại sự kiện</Form.Label>
                                        <Form.Select
                                            value={filter.type}
                                            onChange={e => setFilter(f => ({ ...f, type: e.target.value }))}
                                        >
                                            <option value="">Tất cả</option>
                                            <option value="tournament">Giải đấu</option>
                                            <option value="workshop">Workshop</option>
                                            <option value="festival">Ngày hội</option>
                                            <option value="other">Khác</option>
                                        </Form.Select>
                                    </Col>
                                    <Col md={2}>
                                        <Form.Label>Địa điểm</Form.Label>
                                        <Form.Control
                                            value={filter.location}
                                            onChange={e => setFilter(f => ({ ...f, location: e.target.value }))}
                                            placeholder="Nhập địa điểm"
                                        />
                                    </Col>
                                    <Col md={2}>
                                        <Form.Label>Từ ngày</Form.Label>
                                        <Form.Control
                                            type="date"
                                            value={filter.fromDate}
                                            onChange={e => setFilter(f => ({ ...f, fromDate: e.target.value }))}
                                        />
                                    </Col>
                                    <Col md={2}>
                                        <Form.Label>Đến ngày</Form.Label>
                                        <Form.Control
                                            type="date"
                                            value={filter.toDate}
                                            onChange={e => setFilter(f => ({ ...f, toDate: e.target.value }))}
                                        />
                                    </Col>
                                    <Col md={2} className="d-flex align-items-end">
                                        <Button
                                            variant="outline-secondary"
                                            onClick={() => setFilter({
                                                status: "",
                                                type: "",
                                                location: "",
                                                fromDate: "",
                                                toDate: ""
                                            })}
                                        >
                                            Đặt lại
                                        </Button>
                                    </Col>
                                </Row>
                            )}
                            <Table striped bordered hover responsive>
                                <thead>
                                    <tr>
                                        <th>Tên sự kiện</th>
                                        <th>Thời gian</th>
                                        <th>Địa điểm</th>
                                        <th>Số người tham gia</th>
                                        <th>Trạng thái</th>
                                        <th>Thao tác</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredEvents.map(event => (
                                        <tr key={event.id}>
                                            <td>
                                                <div className="d-flex align-items-center">
                                                    <img src={event.image} alt="" width={40} height={40} className="rounded me-2" />
                                                    <div>
                                                        <div>{event.title}</div>
                                                        <div className="text-muted" style={{ fontSize: 13 }}>{event.type === "tournament" ? "Giải đấu" : event.type === "workshop" ? "Workshop" : event.type === "festival" ? "Ngày hội" : "Khác"}</div>
                                                    </div>
                                                </div>
                                            </td>
                                            <td>
                                                {new Date(event.startDate).toLocaleDateString("vi-VN")}<br />
                                                <span className="text-muted" style={{ fontSize: 13 }}>
                                                    {new Date(event.startDate).toLocaleTimeString("vi-VN", { hour: "2-digit", minute: "2-digit" })} - {new Date(event.endDate).toLocaleTimeString("vi-VN", { hour: "2-digit", minute: "2-digit" })}
                                                </span>
                                            </td>
                                            <td>{event.location}</td>
                                            <td>{event.participants}</td>
                                            <td>
                                                <Badge bg={
                                                    event.status === "Sắp diễn ra" ? "warning" :
                                                        event.status === "Đang diễn ra" ? "success" :
                                                            "secondary"
                                                }>
                                                    {event.status}
                                                </Badge>
                                            </td>
                                            <td>
                                                <Button size="sm" variant="outline-primary" className="me-1">
                                                    Xem
                                                </Button>
                                                <Button size="sm" variant="outline-warning" className="me-1">
                                                    Sửa
                                                </Button>
                                                <Button size="sm" variant="outline-danger">
                                                    Xóa
                                                </Button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </Table>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
}
