import { useState } from "react";
import { Form, Button, Alert, Container, Row, Col } from "react-bootstrap";

export default function EventRegisterForm({ event, onClose }) {
    const [form, setForm] = useState({
        name: "",
        email: "",
        phone: ""
    });
    const [success, setSuccess] = useState(false);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        setSuccess(true);
        setTimeout(() => {
            setSuccess(false);
            if (onClose) onClose();
        }, 2000);
    };

    const [showRegister, setShowRegister] = useState(false);

    if (!event) return <div>Không tìm thấy sự kiện.</div>;

    return (
        <Container fluid className="main-content pb-5">
            <Row>
                <Col md={6}>
                    <img src={event.image} alt={event.title} className="img-fluid rounded-4 mb-3" />
                </Col>
                <Col md={6}>
                    <h2>{event.title}</h2>
                    <Badge bg="info" className="mb-2">{event.status}</Badge>
                    <div className="mb-2"><strong>Thời gian:</strong> {event.date}</div>
                    <div className="mb-2"><strong>Địa điểm:</strong> {event.location}</div>
                    <div className="mb-2"><strong>Khu vực:</strong> {event.region === "north" ? "Miền Bắc" : event.region === "central" ? "Miền Trung" : "Miền Nam"}</div>
                    <div className="mb-2"><strong>Loại:</strong> {event.type === "tournament" ? "Giải đấu" : event.type === "workshop" ? "Workshop" : event.type === "festival" ? "Ngày hội" : "Khác"}</div>
                    <div className="mb-2"><strong>Phí tham gia:</strong> {event.fee === 0 ? "Miễn phí" : event.fee.toLocaleString() + " VNĐ"}</div>
                    <div className="mb-2"><strong>Số người:</strong> {event.participants}/{event.maxParticipants}</div>
                    <div className="mb-2">
                        {event.hasPrize && <Badge bg="success" className="me-2">Có giải thưởng</Badge>}
                        {event.forBeginner && <Badge bg="primary" className="me-2">Cho người mới</Badge>}
                        {event.forFamily && <Badge bg="warning" text="dark">Cho gia đình</Badge>}
                    </div>
                    <p className="mt-3">{event.description}</p>
                    <Button variant="primary" onClick={() => setShowRegister(true)}>
                        Đăng ký tham gia
                    </Button>
                </Col>
            </Row>
            {showRegister && (
                <Row className="mt-4">
                    <Col md={8} className="mx-auto">
                        <EventRegisterForm event={event} onClose={() => setShowRegister(false)} />
                    </Col>
                </Row>
            )}
            <Form onSubmit={handleSubmit} className="bg-white rounded-4 shadow-sm p-4 w-50 w-lg-50 mx-auto">
                <h5 className="mb-3">Đăng ký tham gia sự kiện</h5>
                {success && <Alert variant="success">Đăng ký thành công!</Alert>}
                <Form.Group className="mb-3">
                    <Form.Label>Họ và tên</Form.Label>
                    <Form.Control
                        name="name"
                        value={form.name}
                        onChange={handleChange}
                        required
                    />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        type="email"
                        name="email"
                        value={form.email}
                        onChange={handleChange}
                        required
                    />
                </Form.Group>
                <Form.Group className="mb-3">
                    <Form.Label>Số điện thoại</Form.Label>
                    <Form.Control
                        name="phone"
                        value={form.phone}
                        onChange={handleChange}
                        required
                    />
                </Form.Group>
                <div className="text-end d-flex justify-content-end gap-2">
                    <Button variant="secondary" type="button" onClick={onClose}>Hủy</Button>
                    <Button variant="primary" type="submit">Đăng ký</Button>
                </div>
            </Form>
        </Container>
    );
}
