import { useState } from "react";
import { Form, Button, Alert } from "react-bootstrap";

export default function EventRegisterForm({ event, onClose }) {
    const [form, setForm] = useState({
        name: "",
        email: "",
        phone: ""
    });
    const [success, setSuccess] = useState(false);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Xử lý đăng ký ở đây (gửi API hoặc cập nhật state)
        setSuccess(true);
        setTimeout(() => {
            setSuccess(false);
            if (onClose) onClose();
        }, 2000);
    };

    return (
        <Form onSubmit={handleSubmit} className="bg-light p-4 rounded-4 shadow-sm">
            <h5 className="mb-3">Đăng ký tham gia sự kiện</h5>
            {success && <Alert variant="success">Đăng ký thành công!</Alert>}
            <Form.Group className="mb-3">
                <Form.Label>Họ và tên</Form.Label>
                <Form.Control
                    name="name"
                    value={form.name}
                    onChange={handleChange}
                    required
                />
            </Form.Group>
            <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control
                    type="email"
                    name="email"
                    value={form.email}
                    onChange={handleChange}
                    required
                />
            </Form.Group>
            <Form.Group className="mb-3">
                <Form.Label>Số điện thoại</Form.Label>
                <Form.Control
                    name="phone"
                    value={form.phone}
                    onChange={handleChange}
                    required
                />
            </Form.Group>
            <div className="text-end d-flex justify-content-end gap-2">
                <Button variant="secondary" type="button" onClick={onClose}>Hủy</Button>
                <Button variant="primary" type="submit">Đăng ký</Button>
            </div>
        </Form>
    );
}
