import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { Container, Card, ListGroup, Button } from "react-bootstrap";

export default function EventParticipants() {
    const { id } = useParams();
    const [participants, setParticipants] = useState([]);
    const [eventTitle, setEventTitle] = useState("");

    useEffect(() => {
        // Lấy thông tin sự kiện và danh sách người tham gia
        fetch(`http://localhost:9999/events/${id}`)
            .then(res => res.json())
            .then(event => {
                setEventTitle(event.title);
                setParticipants(event.participantList || []);
            });
    }, [id]);

    return (
        <Container className="py-4">
            <Card>
                <Card.Header>
                    <h5>Danh sách người tham gia: {eventTitle}</h5>
                </Card.Header>
                <Card.Body>
                    {participants.length === 0 ? (
                        <div className="text-muted">Chưa có người tham gia</div>
                    ) : (
                        <ListGroup>
                            {participants.map((user, idx) => (
                                <ListGroup.Item key={user.id || idx}>
                                    {user.name || user.fullName || user.email || "Người dùng"}
                                </ListGroup.Item>
                            ))}
                        </ListGroup>
                    )}
                    <Button as={Link} to="/eventsmanager" variant="secondary" className="mt-3">
                        Quay lại
                    </Button>
                </Card.Body>
            </Card>
        </Container>
    );
}
