import React, { useState, useEffect } from "react";
import {
    Container, Row, Col, Card, Button, Form, InputGroup, Alert,
} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../Auth/AuthContext"; // Thêm dòng này

export default function EventDetail() {
    const { id } = useParams();
    const navigate = useNavigate(); // Thêm dòng này
    const { user } = useAuth(); // Thêm dòng này
    const [event, setEvent] = useState(null);
    const [terms, setTerms] = useState(false);
    const [showAlert, setShowAlert] = useState(false);
    const [form, setForm] = useState({
        name: "",
        phone: "",
        email: "",
        age: "",
        gender: "Nam"
    });
    const [isRegistered, setIsRegistered] = useState(false);

    useEffect(() => {
        fetch(`http://localhost:9999/events/${id}`)
            .then(res => res.json())
            .then(data => setEvent(data));
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!user) {
            navigate("/login");
            return;
        }
        if (!terms) {
            setShowAlert(true);
            return;
        }
        // Lưu thông tin đăng ký vào database, có userId
        const registration = {
            eventId: event.id,
            userId: user.id,
            ...form,
            registeredAt: new Date().toISOString()
        };
        await fetch("http://localhost:9999/registrations", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(registration)
        });

        // Tăng số người tham gia lên 1
        await fetch(`http://localhost:9999/events/${event.id}`, {
            method: "PATCH",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ participants: Number(event.participants) + 1 })
        });

        // Fetch lại event để cập nhật số lượng mới
        await fetch(`http://localhost:9999/events/${event.id}`)
            .then(res => res.json())
            .then(data => setEvent(data));

        alert("Đăng ký thành công! ");

    };

    return (
        <Container className="py-4 main-content">
            <Row>
                <Col lg={5} className="mb-4">
                    <Card>
                        {event && (
                            <>
                                <Card.Img variant="top" src={event.image} style={{ height: 250, objectFit: "cover" }} />
                                <Card.Body>
                                    <Card.Title>{event.title}</Card.Title>
                                    <Card.Text>
                                        <b>Thời gian:</b> {new Date(event.startDate).toLocaleDateString("vi-VN")} - {new Date(event.endDate).toLocaleDateString("vi-VN")}<br />
                                        <b>Địa điểm:</b> {event.location}<br />
                                        <b>Số lượng:</b> {event.participants}/{event.maxParticipants} người đã đăng ký<br />
                                        <b>Trạng thái:</b> {event.status}<br />
                                    </Card.Text>
                                    <hr />
                                    <b>Mô tả sự kiện</b>
                                    <div className="text-muted" style={{ fontSize: 15 }}>
                                        {event.description}
                                    </div>
                                    <hr />
                                    <b>Giải thưởng</b>
                                    {event.hasPrize && event.prizeDetail ? (
                                        <div className="mb-2">{event.prizeDetail}</div>
                                    ) : (
                                        <div className="mb-2 text-muted">Không có giải thưởng</div>
                                    )}
                                    <hr />
                                    <b>Liên hệ</b>
                                    <div>Trần Văn Hùng (Ban tổ chức) - 0912 345 678<br />giaidau@caucahanoi.vn</div>
                                </Card.Body>
                            </>
                        )}
                    </Card>
                </Col>
                <Col lg={7}>
                    <Card>
                        <Card.Body>
                            <h2 className="mb-4">Đăng ký tham gia</h2>
                            {showAlert && (
                                <Alert variant="danger" onClose={() => setShowAlert(false)} dismissible>
                                    Vui lòng đồng ý với điều khoản và điều kiện để tiếp tục.
                                </Alert>
                            )}
                            <Form onSubmit={handleSubmit}>
                                <h5 className="mb-3">Thông tin cá nhân</h5>
                                <Row>
                                    <Col md={6}>
                                        <Form.Group className="mb-3">
                                            <Form.Label>Họ và tên <span className="text-danger">*</span></Form.Label>
                                            <Form.Control
                                                required
                                                type="text"
                                                name="name"
                                                value={form.name}
                                                onChange={handleChange}
                                            />
                                        </Form.Group>
                                    </Col>
                                    <Col md={6}>
                                        <Form.Group className="mb-3">
                                            <Form.Label>Số điện thoại <span className="text-danger">*</span></Form.Label>
                                            <Form.Control
                                                required
                                                type="tel"
                                                name="phone"
                                                value={form.phone}
                                                onChange={handleChange}
                                            />
                                        </Form.Group>
                                    </Col>
                                    <Col md={12}>
                                        <Form.Group className="mb-3">
                                            <Form.Label>Email <span className="text-danger">*</span></Form.Label>
                                            <Form.Control
                                                required
                                                type="email"
                                                name="email"
                                                value={form.email}
                                                onChange={handleChange}
                                            />
                                        </Form.Group>
                                    </Col>
                                    <Col md={6}>
                                        <Form.Group className="mb-3">
                                            <Form.Label>Tuổi</Form.Label>
                                            <Form.Control
                                                type="number"
                                                min={1}
                                                name="age"
                                                value={form.age}
                                                onChange={handleChange}
                                            />
                                        </Form.Group>
                                    </Col>
                                    <Col md={6}>
                                        <Form.Group className="mb-3">
                                            <Form.Label>Giới tính</Form.Label>
                                            <div>
                                                <Form.Check
                                                    inline
                                                    label="Nam"
                                                    name="gender"
                                                    type="radio"
                                                    value="Nam"
                                                    checked={form.gender === "Nam"}
                                                    onChange={handleChange}
                                                />
                                                <Form.Check
                                                    inline
                                                    label="Nữ"
                                                    name="gender"
                                                    type="radio"
                                                    value="Nữ"
                                                    checked={form.gender === "Nữ"}
                                                    onChange={handleChange}
                                                />
                                                <Form.Check
                                                    inline
                                                    label="Khác"
                                                    name="gender"
                                                    type="radio"
                                                    value="Khác"
                                                    checked={form.gender === "Khác"}
                                                    onChange={handleChange}
                                                />
                                            </div>
                                        </Form.Group>
                                    </Col>
                                </Row>

                                <Form.Group className="my-3">
                                    <Form.Check
                                        type="checkbox"
                                        label={<>Tôi đã đọc và đồng ý với <a href="#">Điều khoản và Điều kiện</a> của sự kiện.</>}
                                        checked={terms}
                                        onChange={e => setTerms(e.target.checked)}
                                    />
                                </Form.Group>
                                <div className="d-flex justify-content-end">
                                    <Button type="submit" variant="primary" size="lg">
                                        Xác nhận đăng ký
                                    </Button>
                                </div>
                            </Form>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
}
