import React, { useEffect, useState } from "react";
import { Container, Table, Button, Alert } from "react-bootstrap";
import { useAuth } from "../../pages/Auth/AuthContext";
import { useNavigate } from "react-router-dom";

function OrderConfirm() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [details, setDetails] = useState([]);
  const [status, setStatus] = useState(null);

  useEffect(() => {
    if (!user) return navigate("/login");

    fetch(`http://localhost:9999/orders?userId=${user.id}&_sort=id&_order=desc&_limit=1`)
      .then((res) => res.json())
      .then(async (orders) => {
        if (orders.length === 0) return;
        const latestOrder = orders[0];
        setOrder(latestOrder);

        const resDetails = await fetch(`http://localhost:9999/orderDetails?orderId=${latestOrder.id}`);
        const dataDetails = await resDetails.json();
        setDetails(dataDetails);

        const resStatus = await fetch(`http://localhost:9999/orderStatus/${latestOrder.statusId}`);
        const dataStatus = await resStatus.json();
        setStatus(dataStatus.name);
      })
      .catch((err) => console.error("Error fetching order:", err));
  }, [user]);

  const handleCancel = async () => {
    if (!order) return;
    const res = await fetch(`http://localhost:9999/orders/${order.id}`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ statusId: 5 }), // 5 = Đã hủy
    });

    if (res.ok) {
      setStatus("Đã hủy");
      alert("Đơn hàng đã được hủy.");
    } else {
      alert("Hủy đơn hàng thất bại.");
    }
  };

  return (
    <Container className="mt-5 pt-5">
      <h2 className="mb-4">📦 Order History</h2>

      {!order ? (
        <Alert variant="info">Bạn chưa có đơn hàng nào gần đây.</Alert>
      ) : (
        <>
          <h5 className="mb-2">Mã đơn hàng: #{order.id}</h5>
          <p><strong>Ngày đặt:</strong> {new Date(order.orderDate).toLocaleString()}</p>
          <p><strong>Trạng thái:</strong> {status}</p>

          <Table bordered hover responsive className="mt-4">
            <thead className="table-primary">
              <tr>
                <th>#</th>
                <th>Sản phẩm</th>
                <th>Số lượng</th>
                <th>Đơn giá</th>
                <th>Thành tiền</th>
              </tr>
            </thead>
            <tbody>
              {details.map((item, idx) => (
                <tr key={item.id}>
                  <td>{idx + 1}</td>
                  <td>{item.productId}</td>
                  <td>{item.quantity}</td>
                  <td>{item.unitPrice.toLocaleString()} VND</td>
                  <td>{(item.quantity * item.unitPrice).toLocaleString()} VND</td>
                </tr>
              ))}
            </tbody>
          </Table>

          <div className="text-end mt-3">
            <h5>Tổng tiền: <span className="text-primary">{order.totalAmount.toLocaleString()} VND</span></h5>
            {status !== "Đã hủy" && (
              <Button variant="danger" className="mt-2" onClick={handleCancel}>
                Hủy đơn hàng
              </Button>
            )}
          </div>
        </>
      )}
    </Container>
  );
}

export default OrderConfirm;
