import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Alert,
  Spinner,
  Form,
} from "react-bootstrap";
import { useAuth } from "../Auth/AuthContext";

export default function Shop() {
  const { user } = useAuth();
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState(() => JSON.parse(localStorage.getItem("cart")) || []);
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOption, setSortOption] = useState("default");

  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:9999/shop")
      .then((res) => res.json())
      .then((data) => {
        setProducts(data);
        setFilteredProducts(data);
      })
      .catch((err) => console.error("Error fetching products:", err))
      .finally(() => setLoading(false));
  }, []);

  useEffect(() => {
    let updated = [...products];

    if (searchTerm) {
      updated = updated.filter(p =>
        p.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    switch (sortOption) {
      case "price-asc":
        updated.sort((a, b) => a.price - b.price);
        break;
      case "price-desc":
        updated.sort((a, b) => b.price - a.price);
        break;
      case "name-asc":
        updated.sort((a, b) => a.name.localeCompare(b.name));
        break;
      default:
        break;
    }

    setFilteredProducts(updated);
  }, [searchTerm, sortOption, products]);

  const updateCart = (newCart) => {
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
    window.dispatchEvent(new CustomEvent("cartUpdated"));
  };

  const handleBuyClick = (product) => {
    if (!user) {
      navigate("/login");
      return;
    }

    const existingCart = [...cart];
    const index = existingCart.findIndex((item) => item.id === product.id);

    if (index >= 0) {
      existingCart[index].quantity += 1;
    } else {
      existingCart.push({ ...product, quantity: 1 });
    }

    updateCart(existingCart);
  };

  return (
    <Container className="pt-5 mt-5">
      <h1 className="text-center fw-bold mb-4">üì¶ S·∫£n ph·∫©m</h1>

      <Row className="mb-4 gx-3">
        <Col xs={12} md={9} className="mb-2">
          <Form.Control
            type="text"
            placeholder="üîç T√¨m ki·∫øm s·∫£n ph·∫©m..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="shadow-sm rounded-pill px-4 py-3 fs-5"
            style={{ height: "56px" }}
          />
        </Col>
        <Col xs={12} md={3} className="mb-2">
         <Form.Select
  value={sortOption}
  onChange={(e) => setSortOption(e.target.value)}
  className="rounded-pill py-3 px-3 fs-5 shadow-sm"
  style={{ height: "56px", lineHeight: "1.7em" }} // üëà fix b·ªã khu·∫•t icon
>
  <option value="default">üî• S·∫£n ph·∫©m n·ªïi b·∫≠t</option>
  <option value="price-asc">‚¨ÜÔ∏è Gi√° tƒÉng d·∫ßn</option>
  <option value="price-desc">‚¨áÔ∏è Gi√° gi·∫£m d·∫ßn</option>
  <option value="name-asc">üî§ T√™n A-Z</option>
  <option value="name-desc">üî° T√™n Z-A</option>
</Form.Select>
        </Col>
      </Row>

      {loading ? (
        <div className="d-flex justify-content-center my-5">
          <Spinner animation="border" />
        </div>
      ) : filteredProducts.length === 0 ? (
        <Alert variant="warning">Kh√¥ng t√¨m th·∫•y s·∫£n ph·∫©m ph√π h·ª£p.</Alert>
      ) : (
        <Row xs={1} sm={2} md={3} lg={4} className="gy-4 gx-4">
          {filteredProducts.map((product) => (
            <Col key={product.id}>
              <Card className="h-100 shadow-sm border-0 rounded-4 p-2 card-hover-effect">
                <Card.Img
                  variant="top"
                  src={product.image}
                  alt={product.name}
                  className="rounded-4"
                  style={{ height: "180px", objectFit: "cover" }}
                />
                <Card.Body className="d-flex flex-column">
                  <Card.Title className="fw-semibold mb-2 text-dark">
                    {product.name}
                  </Card.Title>
                  <Card.Text className="text-muted mb-3" style={{ fontSize: "0.9rem" }}>
                    {product.description}
                  </Card.Text>
                  <div className="mt-auto">
                    <p className="mb-1 fw-semibold text-primary">
                      Gi√°: {product.price.toLocaleString()} VND
                    </p>
                    <p className="mb-3 text-secondary" style={{ fontSize: "0.85rem" }}>
                      C√≤n l·∫°i: {product.StockQuantity}
                    </p>
                    <Button
                      variant="primary"
                      className="w-100 rounded-pill fw-semibold shadow-sm"
                      onClick={() => handleBuyClick(product)}
                    >
                      üõí Th√™m v√†o gi·ªè
                    </Button>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
}
