import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // React Router v6+

export default function Shop() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:3001/shop")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Error fetching products:", err));
  }, []);

  const handleBuyClick = (product) => {
    const user = JSON.parse(localStorage.getItem("user"));

    if (!user) {
      alert("Bạn cần đăng nhập để mua sản phẩm.");
      navigate("/login");
      return;
    }

    // Get current cart from localStorage or empty array
    const existingCart = JSON.parse(localStorage.getItem("cart")) || [];

    // Check if product already exists in cart
    const index = existingCart.findIndex((item) => item.id === product.id);
    if (index >= 0) {
      existingCart[index].quantity += 1;
    } else {
      existingCart.push({ ...product, quantity: 1 });
    }

    // Save back to localStorage
    localStorage.setItem("cart", JSON.stringify(existingCart));
    alert("Đã thêm vào giỏ hàng!");
  };

  return (
    <div className="main-content">
      <h1>Shop</h1>
      <h2>Sản phẩm</h2>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "24px" }}>
        {products.map((product) => (
          <div
            key={product.id}
            style={{
              border: "1px solid #ccc",
              padding: 16,
              width: 250,
              borderRadius: 8,
              boxShadow: "0 2px 6px rgba(0, 0, 0, 0.1)",
            }}
          >
            <img
              src={product.image}
              alt={product.name}
              style={{
                width: "100%",
                height: 150,
                objectFit: "cover",
                borderRadius: 4,
              }}
            />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>
              <strong>Giá:</strong> {product.price.toLocaleString()} VND
            </p>
            <p>
              <strong>Còn lại:</strong> {product.StockQuantity}
            </p>
            <button
              onClick={() => handleBuyClick(product)}
              style={{
                marginTop: 8,
                width: "100%",
                padding: "8px",
                backgroundColor: "#007bff",
                color: "white",
                border: "none",
                borderRadius: 4,
                cursor: "pointer",
              }}
            >
              Mua
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
